mapper.select:


    and gw_dev_code = #{gwDevCode}
     and device_name like concat('%', #{deviceName}, '%')
     and product_id = #{productId}
     and product_name like concat('%', #{productName}, '%')
     and tenant_id = #{tenantId}
     and tenant_name like concat('%', #{tenantName}, '%')
     and serial_number = #{serialNumber}
     and status = #{status}
     and network_address like concat('%', #{networkAddress}, '%')

        and tenant_id in (SELECT u.user_id FROM sys_user u
        join sys_dept de on u.dept_id = de.dept_id
        WHERE FIND_IN_SET(#{deptId}, de.ancestors) OR de.dept_id = #{deptId})


        and tenant_id = (SELECT dept_user_id FROM sys_dept WHERE dept_id = #{deptId})

     and active_time between #{params.beginActiveTime} and #{params.endActiveTime}

order by create_time desc

select d.device_id, d.device_name, d.product_id, d.product_name, d.tenant_id, d.tenant_name,
d.serial_number,d.gw_dev_code, d.firmware_version, d.status,d.is_shadow,d.is_simulate ,d.location_way,d.active_time, d.img_url,a.device_id as auth_device_id
from iot_device d
left join iot_product_authorize a on a.device_id=d.device_id

     and ISNULL(a.device_id)
     and d.device_name like concat('%', #{deviceName}, '%')
     and d.product_id = #{productId}
     and d.product_name like concat('%', #{productName}, '%')
     and d.tenant_id = #{tenantId}
     and d.tenant_name like concat('%', #{tenantName}, '%')
     and d.serial_number = #{serialNumber}
    and d.gw_dev_code = #{gwDevCode,jdbcType=VARCHAR}
     and d.status = #{status}

        and d.tenant_id in (SELECT u.user_id FROM sys_user u
        join sys_dept de on u.dept_id = de.dept_id
        WHERE FIND_IN_SET(#{deptId}, de.ancestors) OR de.dept_id = #{deptId})


        and d.tenant_id = (SELECT dept_user_id FROM sys_dept WHERE dept_id = #{deptId})

     and d.active_time between #{params.beginActiveTime} and #{params.endActiveTime}

order by d.create_time desc

SELECT d.device_id, d.device_name, d.product_name, d.serial_number, d.firmware_version, d.wireless_version, d.STATUS, d.rssi, d.is_shadow, d.location_way, d.active_time, d.network_address, d.longitude, d.latitude FROM iot_device d
and d.product_id = #{productId}
and d.device_name like concat('%', #{deviceName}, '%')
and d.product_name like concat('%', #{productName}, '%')
and d.serial_number = #{serialNumber}
and d.status = #{status}
and d.network_address like concat('%', #{networkAddress}, '%')
and d.active_time between #{params.beginActiveTime} and #{params.endActiveTime}
and d.tenant_id in (SELECT u.user_id FROM sys_user u WHERE u.dept_id IN ( SELECT dept_id FROM sys_dept WHERE find_in_set(#{deptId}, ancestors) OR dept_id = #{deptId}))
and d.tenant_id = (SELECT dept_user_id FROM sys_dept WHERE dept_id = #{deptId})
and firmware_version = #{firmwareVersion}
and d.wireless_version = #{wirelessVersion}
group by d.device_id,d.tenant_id order by d.create_time desc

select d.device_id, d.device_name, p.product_id, p.product_name,p.device_type,
    d.tenant_id, d.tenant_name, d.serial_number,d.gw_dev_code,
    d.firmware_version, d.status,d.rssi,d.is_shadow,d.is_simulate ,d.location_way,
    d.things_model_value, d.active_time,d.create_time, if(null = d.img_url or '' = d.img_url, p.img_url, d.img_url) as img_url,
    case
       when (select count(device_id) from iot_device_share du where du.device_id = d.device_id and du.user_id = #{tenantId}) then 0
       else 1
       end as is_owner,
    (select count(1) from iot_device d1 where d1.gw_dev_code = d.serial_number) as sub_device_count,
p.protocol_code,p.transport,p.guid
from iot_device d
left join iot_product p on p.product_id=d.product_id
 left join iot_device_group g on g.device_id=d.device_id 

    and d.gw_dev_code is null
     and g.group_id = #{groupId}
     and d.device_name like concat('%', #{deviceName}, '%')
     and d.product_id = #{productId}
     and p.device_type = #{deviceType}
     and d.product_name like concat('%', #{productName}, '%')
     and d.serial_number = #{serialNumber}
     and d.gw_dev_code = #{gwDevCode}
     and d.status = #{status}
    and d.is_simulate = #{isSimulate}

        and
        (   d.tenant_id in (SELECT u.user_id FROM sys_user u
                join sys_dept de on u.dept_id = de.dept_id
                WHERE FIND_IN_SET(#{deptId}, de.ancestors) OR de.dept_id = #{deptId})
            or d.device_id in (select du.device_id from iot_device_share du where du.user_id = #{tenantId})
        )


        and (
            d.tenant_id = (SELECT dept_user_id FROM sys_dept WHERE dept_id = #{deptId})
            or d.device_id in (select du.device_id from iot_device_share du where du.user_id = #{tenantId})
        )

     and d.active_time between #{params.beginActiveTime} and #{params.endActiveTime}

group by d.device_id,d.tenant_id
order by d.create_time desc

mapper.update:
update iot_device
device_name = #{deviceName}, product_id = #{productId}, product_name = #{productName}, tenant_id = #{tenantId}, tenant_name = #{tenantName}, serial_number = #{serialNumber}, gw_dev_code = #{gwDevCode}, firmware_version = #{firmwareVersion}, status = #{status}, rssi = #{rssi}, is_shadow = #{isShadow}, is_simulate = #{isSimulate}, location_way = #{locationWay}, things_model_value = #{thingsModelValue}, network_address = #{networkAddress}, network_ip = #{networkIp}, longitude = #{longitude}, latitude = #{latitude}, active_time = #{activeTime}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, img_url = #{imgUrl}, summary = #{summary}, slave_id = #{slaveId},
where device_id = #{deviceId}

update iot_device
status = #{status}, network_address = #{networkAddress}, network_ip = #{networkIp}, longitude = #{longitude}, latitude = #{latitude}, active_time = #{activeTime}, update_time = #{updateTime,jdbcType=TIMESTAMP}
where serial_number = #{serialNumber} or gw_dev_code = #{serialNumber}

update iot_device
    set firmware_version = #{firmwareVersion,jdbcType=DECIMAL}
where serial_number = #{serialNumber,jdbcType=VARCHAR}

update iot_device
device_name = #{deviceName}, product_id = #{productId}, product_name = #{productName}, tenant_id = #{tenantId}, tenant_name = #{tenantName}, serial_number = #{serialNumber}, firmware_version = #{firmwareVersion}, status = #{status}, rssi = #{rssi}, is_shadow = #{isShadow}, is_simulate = #{isSimulate}, location_way = #{locationWay}, things_model_value = #{thingsModelValue}, network_address = #{networkAddress}, network_ip = #{networkIp}, longitude = #{longitude}, latitude = #{latitude}, active_time = #{activeTime}, del_flag = #{delFlag}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark}, img_url = #{imgUrl}, summary = #{summary}, gw_dev_code = #{gwDevCode},
where serial_number = #{serialNumber}

mapper.delete:
delete from iot_device_group

     and device_id = #{deviceId}
     and group_id in(select group_id from iot_group where user_id = #{userId})

mapper.select:
select device_id, serial_number, device_name
from iot_device
where serial_number = #{deviceNumber}

select d.device_id, d.device_name, d.product_id, d.product_name,p.device_type,
d.tenant_id, d.tenant_name, d.serial_number,d.gw_dev_code,
d.firmware_version, d.status,d.rssi,d.is_shadow,d.is_simulate ,d.location_way,
d.things_model_value, d.active_time,d.create_time,d.img_url,
(select count(1) from iot_device d1 where d1.gw_dev_code = d.serial_number) as sub_device_count,
p.protocol_code,p.transport, u.is_owner
from (
        select device_id, 1 AS is_owner
        from iot_device_user
        where user_id = #{tenantId}
        union
        select device_id, 0 AS is_owner
        from iot_device_share
        where user_id = #{tenantId}
) as u
inner join iot_device d on u.device_id = d.device_id
inner join iot_product p on d.product_id = p.product_id
 left join iot_device_group g on g.device_id=d.device_id 

    and d.gw_dev_code is null
     and g.group_id = #{groupId}
     and d.device_name like concat('%', #{deviceName}, '%')
     and d.product_id = #{productId}
     and p.device_type = #{deviceType}
     and d.product_name like concat('%', #{productName}, '%')
     and d.serial_number = #{serialNumber}
     and d.gw_dev_code = #{gwDevCode}
     and d.status = #{status}
    and d.is_simulate = #{isSimulate}
     and d.active_time between #{params.beginActiveTime} and #{params.endActiveTime}

order by u.is_owner desc, d.create_time desc

select d.device_id, d.device_name, d.product_name, d.serial_number,d.gw_dev_code, d.firmware_version,d.wireless_version, d.status,d.rssi,d.is_shadow ,
d.location_way, d.active_time,d.network_address,d.longitude,d.latitude
from (
        select device_id, 1 AS is_owner
        from iot_device_user
        where user_id = #{tenantId}
        union
        select device_id, 0 AS is_owner
        from iot_device_share
        where user_id = #{tenantId}
) as u
inner join iot_device d on u.device_id = d.device_id
inner join iot_product p on d.product_id = p.product_id


        and d.wireless_version = #{wirelessVersion}

     and d.device_name like concat('%', #{deviceName}, '%')

order by u.is_owner desc, d.create_time desc

mapper.update:
